changeSet.sql:
-- Таблицы
GRANT ALL PRIVILEGES ON ALL TABLES IN SCHEMA public TO app_admin, db_admin;

-- Sequence
GRANT ALL PRIVILEGES ON ALL SEQUENCES IN SCHEMA public TO app_admin, db_admin;

-- чтобы новые таблицы не ломались
ALTER DEFAULT PRIVILEGES IN SCHEMA public
    GRANT ALL ON TABLES TO app_admin, db_admin;

ALTER DEFAULT PRIVILEGES IN SCHEMA public
    GRANT ALL ON SEQUENCES TO app_admin, db_admin;
